// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.12;

import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

import "../App.sol";
import "../ERC721Properties/ERC721Properties.sol";
import "../../libraries/Exceptions.sol";
import "../../libraries/AppsIds.sol";
import {__with_semver} from "../../libraries/Semver.sol";

contract ERC721WhitelistClaim is App, __with_semver(uint8(1), uint8(0), uint8(1)) {
    string public constant CLAIM_LIMIT_ERROR = "CLAIM_LIMIT_ERROR";
    uint8 public immutable CONTROLLER = _initNextRole();

    bool public isActive;

    bytes public merkleTreeURI; // ipfs hash -> bytes32
    bytes32 public merkleTreeRoot;

    mapping(bytes32 => uint256) public claimLimitByTier;
    mapping(address => mapping(bytes32 => uint256)) public claims;

    uint256[95] private __gap;

    constructor(address trustedForwarder) ERC2771Context(trustedForwarder) {}

    function initialize(
        IKernel _kernel,
        bytes32[] calldata tiers,
        uint256[] calldata claimLimits
    ) external initializer {
        __App_init(_kernel);
        for (uint256 i; i != tiers.length; ++i) {
            claimLimitByTier[tiers[i]] = claimLimits[i];
        }
    }

    function setActive(bool isActive_) external requirePermission(CONTROLLER) {
        isActive = isActive_;
    }

    function setMerkleTree(bytes32 merkleTreeRoot_, bytes calldata merkleTreeURI_)
        external
        requirePermission(CONTROLLER)
    {
        _requireActive();
        merkleTreeRoot = merkleTreeRoot_;
        merkleTreeURI = merkleTreeURI_;
    }

    function claim(
        address to,
        bytes32[] calldata merkleProof,
        bytes32 tierValue
    ) external {
        _requireActive();
        require((++claims[to][tierValue]) <= claimLimitByTier[tierValue], CLAIM_LIMIT_ERROR);
        bytes32 element = keccak256(abi.encodePacked(to, _bytes32ToString(tierValue)));
        require(MerkleProof.verify(merkleProof, merkleTreeRoot, element), Exceptions.VALIDATION_ERROR);
        ERC721Properties(kernel.getAppAddress(AppsIds.ERC721)).mint(to, tierValue);
    }

    function _requireActive() internal view {
        require(isActive, Exceptions.NOT_ACTIVE_ERROR);
    }

    function _bytes32ToString(bytes32 tier) internal pure returns (string memory) {
        uint8 ptr;
        while (ptr < 32 && tier[ptr] != 0) {
            ++ptr;
        }
        bytes memory tmp = new bytes(ptr);
        for (uint8 i; i != ptr; ++i) {
            tmp[i] = tier[i];
        }
        return string(tmp);
    }
}

contracts/CallForwarder/CallForwarder.so.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.12;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./EIP712.sol";
import "../libraries/Exceptions.sol";
import {__with_semver} from "../libraries/Semver.sol";

contract CallForwarder is EIP712, __with_semver(uint8(1), uint8(0), uint8(0)) {
    using ECDSA for bytes32;

    struct ForwardRequest {
        address from; // Externally-owned account (EOA) making the request.
        address to; // Destination address, normally a smart contract.
        uint256 value; // Amount of ether to transfer to the destination.
        uint256 nonce; // On-chain tracked nonce of a transaction.
        bytes data; // (Call)data to be sent to the destination.
    }

    bytes32 private constant _TYPEHASH =
        keccak256("ForwardRequest(address from,address to,uint256 value,uint256 nonce,bytes data)");

    mapping(address => uint256) private _nonces;

    event MetaTransactionExecuted(address indexed from, address indexed to, bytes indexed data);

    constructor(string memory name, string memory version) EIP712(name, version) {}

    /**
     * @dev Returns the domain separator used in the encoding of the signature for `execute`, as defined by {EIP712}.
     * See https://eips.ethereum.org/EIPS/eip-712
     */
    function DOMAIN_SEPARATOR() external view returns (bytes32) {
        return _domainSeparatorV4();
    }

    /// @dev Retrieves the on-chain tracked nonce of an EOA making the request.
    function getNonce(address from) public view returns (uint256) {
        return _nonces[from];
    }

    function getSigner(ForwardRequest calldata req, bytes calldata signature) public view returns (address signer) {
        signer = getMessageHash(req).recover(signature);
    }

    function getMessageHash(ForwardRequest calldata req) public view returns (bytes32) {
        return
            _hashTypedDataV4(
                keccak256(abi.encode(_TYPEHASH, req.from, req.to, req.value, req.nonce, keccak256(req.data)))
            );
    }

    function verify(ForwardRequest calldata req, bytes calldata signature) public view returns (bool) {
        address signer = getSigner(req, signature);
        return _nonces[req.from] == req.nonce && signer == req.from;
    }

    function executeSingle(ForwardRequest calldata req, bytes calldata signature)
        public
        payable
        returns (bytes memory returnData)
    {
        require(verify(req, signature), "AwlForwarder: signature does not match request");
        _nonces[req.from]++;
        returnData = Address.functionCallWithValue(
            req.to,
            abi.encodePacked(req.data, req.from),
            req.value,
            Exceptions.INVALID_AUTHORIZATION_ERROR
        );

        emit MetaTransactionExecuted(req.from, req.to, req.data);
    }

    function executeSingle(ForwardRequest calldata req) public payable returns (bytes memory returnData) {
        require(req.from == msg.sender, "AwlForwarder: msg.sender must be equals with req.from");
        returnData = Address.functionCallWithValue(
            req.to,
            abi.encodePacked(req.data, req.from),
            req.value,
            Exceptions.INVALID_AUTHORIZATION_ERROR
        );
    }

    function executeBatch(ForwardRequest[] calldata reqs, bytes[] calldata signatures)
        public
        payable
        returns (bytes[] memory)
    {
        require(
            reqs.length == signatures.length,
            "length of forward requests must be similar with length of signatures"
        );
        bytes[] memory results = new bytes[](reqs.length);
        for (uint256 i = 0; i < results.length; i++) {
            results[i] = executeSingle(reqs[i], signatures[i]);
        }
        return results;
    }

    function executeBatch(ForwardRequest[] calldata reqs) public payable returns (bytes[] memory) {
        bytes[] memory results = new bytes[](reqs.length);
        for (uint256 i = 0; i < results.length; i++) {
            results[i] = executeSingle(reqs[i]);
